import React, { useState } from "react";

function ProductForm() {
  // Hardcoded categories & suppliers
  const [categories] = useState([
    { _id: 1, name: "Electronics" },
    { _id: 2, name: "Clothing" },
    { _id: 3, name: "Furniture" },
    { _id: 4, name: "Groceries" }
  ]);

  const [suppliers] = useState([
    { _id: 1, name: "ABC Suppliers" },
    { _id: 2, name: "XYZ Traders" },
    { _id: 3, name: "PQR Distributors" }
  ]);

  return (
    <div>
      <h2>Add Product</h2>

      {/* Category dropdown */}
      <div>
        <label>Category *</label>
        <select defaultValue="">
          <option value="" disabled>
            Select category
          </option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      {/* Supplier dropdown */}
      <div>
        <label>Supplier</label>
        <select defaultValue="">
          <option value="" disabled>
            Select supplier
          </option>
          {suppliers.map((sup) => (
            <option key={sup._id} value={sup._id}>
              {sup.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default ProductForm;
