import React, { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { HiX } from 'react-icons/hi';
import axios from 'axios';
import toast from 'react-hot-toast';

const ProductModal = ({ product, categories, suppliers, onClose, onSaved }) => {
  const [loading, setLoading] = useState(false);
  const [generatingBarcode, setGeneratingBarcode] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    setValue,
  } = useForm();

  useEffect(() => {
    if (product) {
      const safe = {
        ...product,
        category: product.category?._id || product.category || '',
        supplier: product.supplier?._id || product.supplier || '',
        location: {
          warehouse: product.location?.warehouse || '',
          aisle: product.location?.aisle || '',
          shelf: product.location?.shelf || '',
          bin: product.location?.bin || '',
        },
      };
      ['_id', '__v', 'createdAt', 'updatedAt'].forEach((k) => delete safe[k]);
      reset(safe);
    } else {
      reset({
        name: '',
        sku: '',
        description: '',
        category: '',
        supplier: '',
        unit: '',
        costPrice: '',
        sellingPrice: '',
        minStockLevel: '',
        maxStockLevel: '',
        barcode: '',
        tags: '',
        location: {
          warehouse: '',
          aisle: '',
          shelf: '',
          bin: '',
        },
      });
    }
  }, [product, reset]);

  const generateBarcode = async () => {
    setGeneratingBarcode(true);
    try {
      const response = await axios.get('/api/barcodes/generate/new');
      setValue('barcode', response.data?.barcode || '');
      toast.success('Barcode generated');
    } catch {
      toast.error('Failed to generate barcode');
    } finally {
      setGeneratingBarcode(false);
    }
  };

  const onSubmit = async (data) => {
    if (!data.category) {
      toast.error('Please select a category');
      return;
    }
    if (!data.location?.warehouse) {
      toast.error('Please provide warehouse location');
      return;
    }

    setLoading(true);
    const payload = {
      name: data.name?.trim(),
      sku: data.sku?.trim(),
      description: data.description || '',
      category: data.category,
      supplier: data.supplier || undefined,
      unit: data.unit || '',
      costPrice: data.costPrice === '' ? undefined : Number(data.costPrice),
      sellingPrice: data.sellingPrice === '' ? undefined : Number(data.sellingPrice),
      minStockLevel: data.minStockLevel === '' ? undefined : Number(data.minStockLevel),
      maxStockLevel: data.maxStockLevel === '' ? undefined : Number(data.maxStockLevel),
      barcode: data.barcode || '',
      tags: data.tags || '',
      location: {
        warehouse: data.location.warehouse,
        aisle: data.location.aisle || '',
        shelf: data.location.shelf || '',
        bin: data.location.bin || '',
      },
    };

    try {
      if (product) {
        await axios.put(`/api/products/${product._id}`, payload);
      } else {
        await axios.post('/api/products', payload);
      }
      onSaved();
    } catch (error) {
      const msg =
        error.response?.data?.msg ||
        error.response?.data?.message ||
        'Failed to save product';
      toast.error(msg);
      console.error('Save product error:', error.response?.data || error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">{product ? 'Edit Product' : 'Add New Product'}</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <HiX className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          {/* Product Name & SKU */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Product Name *</label>
              <input
                type="text"
                {...register('name', { required: 'Product name is required' })}
                className="mt-1 input"
                placeholder="Enter product name"
              />
              {errors.name && <p className="mt-1 text-sm text-danger-600">{errors.name.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">SKU *</label>
              <input
                type="text"
                {...register('sku', { required: 'SKU is required' })}
                className="mt-1 input"
                placeholder="Enter SKU"
              />
              {errors.sku && <p className="mt-1 text-sm text-danger-600">{errors.sku.message}</p>}
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Description</label>
            <textarea
              {...register('description')}
              rows={3}
              className="mt-1 input"
              placeholder="Enter product description"
            />
          </div>

          {/* Category & Supplier */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Category *</label>
              <select {...register('category', { required: 'Category is required' })} className="mt-1 input">
                <option value="">Select category</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>
                    {cat.name}
                  </option>
                ))}
              </select>
              {errors.category && <p className="mt-1 text-sm text-danger-600">{errors.category.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Supplier</label>
              <select {...register('supplier')} className="mt-1 input">
                <option value="">Select supplier</option>
                {suppliers.map((sup) => (
                  <option key={sup._id} value={sup._id}>
                    {sup.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Unit, Cost Price, Selling Price */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Unit *</label>
              <select {...register('unit', { required: 'Unit is required' })} className="mt-1 input">
                <option value="">Select unit</option>
                <option value="piece">Piece</option>
                <option value="kg">Kilogram</option>
                <option value="liter">Liter</option>
                <option value="meter">Meter</option>
                <option value="box">Box</option>
                <option value="pack">Pack</option>
                <option value="dozen">Dozen</option>
                <option value="pair">Pair</option>
              </select>
              {errors.unit && <p className="mt-1 text-sm text-danger-600">{errors.unit.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Cost Price *</label>
              <input
                type="number"
                step="0.01"
                {...register('costPrice', {
                  required: 'Cost price is required',
                  min: { value: 0, message: 'Cost price must be positive' },
                })}
                className="mt-1 input"
                placeholder="0.00"
              />
              {errors.costPrice && <p className="mt-1 text-sm text-danger-600">{errors.costPrice.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Selling Price *</label>
              <input
                type="number"
                step="0.01"
                {...register('sellingPrice', {
                  required: 'Selling price is required',
                  min: { value: 0, message: 'Selling price must be positive' },
                })}
                className="mt-1 input"
                placeholder="0.00"
              />
              {errors.sellingPrice && <p className="mt-1 text-sm text-danger-600">{errors.sellingPrice.message}</p>}
            </div>
          </div>

          {/* Stock Levels */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Minimum Stock Level</label>
              <input
                type="number"
                {...register('minStockLevel', {
                  min: { value: 0, message: 'Minimum stock level must be positive' },
                })}
                className="mt-1 input"
                placeholder="10"
              />
              {errors.minStockLevel && <p className="mt-1 text-sm text-danger-600">{errors.minStockLevel.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Maximum Stock Level</label>
              <input
                type="number"
                {...register('maxStockLevel', {
                  min: { value: 0, message: 'Maximum stock level must be positive' },
                })}
                className="mt-1 input"
                placeholder="100"
              />
              {errors.maxStockLevel && <p className="mt-1 text-sm text-danger-600">{errors.maxStockLevel.message}</p>}
            </div>
          </div>

          {/* Warehouse Location */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Warehouse Location *</label>
            <input
              type="text"
              {...register('location.warehouse', { required: 'Please provide warehouse location' })}
              className="mt-1 input"
              placeholder="Enter warehouse location"
            />
            {errors.location?.warehouse && (
              <p className="mt-1 text-sm text-danger-600">{errors.location.warehouse.message}</p>
            )}
          </div>

          {/* Barcode and Tags */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Barcode</label>
              <div className="flex space-x-2">
                <input
                  type="text"
                  {...register('barcode')}
                  className="mt-1 input flex-1"
                  placeholder="Enter barcode or generate one"
                />
                <button
                  type="button"
                  onClick={generateBarcode}
                  disabled={generatingBarcode}
                  className="mt-1 btn btn-outline"
                >
                  {generatingBarcode ? 'Generating...' : 'Generate'}
                </button>
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Tags</label>
              <input
                type="text"
                {...register('tags')}
                className="mt-1 input"
                placeholder="Enter tags separated by commas"
              />
            </div>
          </div>

          {/* Submit and Cancel Buttons */}
          <div className="flex justify-end space-x-3 pt-4">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" disabled={loading} className="btn btn-primary">
              {loading ? 'Saving...' : product ? 'Update Product' : 'Add Product'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductModal;
