import React, { useState } from "react";

function ProductForm() {
  // Hardcoded categories & suppliers
  const [categories] = useState([
    { _id: 1, name: "Electronics" },
    { _id: 2, name: "Clothing" },
    { _id: 3, name: "Furniture" },
    { _id: 4, name: "Groceries" },
  ]);

  const [suppliers] = useState([
    { _id: 1, name: "ABC Suppliers" },
    { _id: 2, name: "XYZ Traders" },
    { _id: 3, name: "PQR Distributors" },
  ]);

  // State for selected values
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSupplier, setSelectedSupplier] = useState("");

  // Helper functions to get the selected name
  const getCategoryName = (id) => {
    const cat = categories.find((c) => String(c._id) === String(id));
    return cat ? cat.name : "";
  };

  const getSupplierName = (id) => {
    const sup = suppliers.find((s) => String(s._id) === String(id));
    return sup ? sup.name : "";
  };

  return (
    <div>
      <h2>Add Product</h2>

      {/* Category dropdown */}
      <div>
        <label>Category *</label>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Select category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      {/* Supplier dropdown */}
      <div>
        <label>Supplier</label>
        <select
          value={selectedSupplier}
          onChange={(e) => setSelectedSupplier(e.target.value)}
        >
          <option value="">Select supplier</option>
          {suppliers.map((sup) => (
            <option key={sup._id} value={sup._id}>
              {sup.name}
            </option>
          ))}
        </select>
      </div>

      {/* Show selected values */}
      <div style={{ marginTop: "10px" }}>
        <p>Selected Category: {getCategoryName(selectedCategory)}</p>
        <p>Selected Supplier: {getSupplierName(selectedSupplier)}</p>
      </div>
    </div>
  );
}

export default ProductForm;
