import React, { useState, useEffect } from 'react';
import { HiPlus, HiQrcode, HiPencil, HiTrash, HiEye } from 'react-icons/hi';
import axios from 'axios';
import toast from 'react-hot-toast';
import ProductModal from './ProductModal';
import ProductDetailModal from './ProductDetailModal';
import BarcodeScanner from './BarcodeScanner';
import LoadingSpinner from '../UI/LoadingSpinner';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showScanner, setShowScanner] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const [categories, setCategories] = useState([]);
  const [categoriesLoading, setCategoriesLoading] = useState(true);
  const [suppliers, setSuppliers] = useState([]);
  const [suppliersLoading, setSuppliersLoading] = useState(true);

  // New states for adding categories and suppliers
  const [showAddCategory, setShowAddCategory] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState('');
  const [addingCategory, setAddingCategory] = useState(false);

  const [showAddSupplier, setShowAddSupplier] = useState(false);
  const [newSupplierName, setNewSupplierName] = useState('');
  const [addingSupplier, setAddingSupplier] = useState(false);

  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchSuppliers();
  }, [currentPage, searchTerm]);

  useEffect(() => {
    console.log('Fetched Categories:', categories);
    console.log('Fetched Suppliers:', suppliers);
  }, [categories, suppliers]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 10,
        search: searchTerm,
      });
      const response = await axios.get(`/api/products?${params}`);
      setProducts(response.data.products ?? []);
      setTotalPages(response.data.totalPages ?? 1);
    } catch (error) {
      toast.error('Failed to fetch products');
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    setCategoriesLoading(true);
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data ?? []);
    } catch (error) {
      console.error('Error fetching categories:', error);
    } finally {
      setCategoriesLoading(false);
    }
  };

  const fetchSuppliers = async () => {
    setSuppliersLoading(true);
    try {
      const response = await axios.get('/api/suppliers');
      setSuppliers(response.data ?? []);
    } catch (error) {
      console.error('Error fetching suppliers:', error);
    } finally {
      setSuppliersLoading(false);
    }
  };

  const addCategory = async () => {
    if (!newCategoryName.trim()) {
      toast.error('Category name is required');
      return;
    }
    setAddingCategory(true);
    try {
      await axios.post('/api/categories', { name: newCategoryName });
      toast.success('Category added successfully');
      setNewCategoryName('');
      setShowAddCategory(false);
      fetchCategories();
    } catch (error) {
      toast.error('Failed to add category');
      console.error(error);
    } finally {
      setAddingCategory(false);
    }
  };

  const addSupplier = async () => {
    if (!newSupplierName.trim()) {
      toast.error('Supplier name is required');
      return;
    }
    setAddingSupplier(true);
    try {
      await axios.post('/api/suppliers', { name: newSupplierName });
      toast.success('Supplier added successfully');
      setNewSupplierName('');
      setShowAddSupplier(false);
      fetchSuppliers();
    } catch (error) {
      toast.error('Failed to add supplier');
      console.error(error);
    } finally {
      setAddingSupplier(false);
    }
  };

  const handleAddProduct = () => {
    if (!categoriesLoading && !suppliersLoading) {
      setSelectedProduct(null);
      setShowModal(true);
    } else {
      toast('Please wait, loading categories and suppliers...');
    }
  };

  // ... other handlers omitted for brevity (handleEditProduct, handleDeleteProduct, ...)

  if (loading) return <LoadingSpinner size="lg" className="h-64" />;

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold">Products</h1>
          <p className="text-gray-600">Manage your product inventory</p>
        </div>
        <div className="mt-4 sm:mt-0 flex space-x-3">
          <button onClick={() => setShowScanner(true)} className="btn btn-outline">
            <HiQrcode className="w-4 h-4 mr-2" /> Scan Barcode
          </button>
          <button onClick={handleAddProduct} className="btn btn-primary">
            <HiPlus className="w-4 h-4 mr-2" /> Add Product
          </button>
        </div>
      </div>

      {/* Add Category and Supplier Buttons */}
      <div className="flex space-x-4 mb-4">
        <div>
          <button onClick={() => setShowAddCategory(true)} className="btn btn-outline btn-sm">
            + Add New Category
          </button>
          {showAddCategory && (
            <div className="mt-2">
              <input
                type="text"
                value={newCategoryName}
                onChange={(e) => setNewCategoryName(e.target.value)}
                placeholder="Category name"
                className="input input-sm"
              />
              <button onClick={addCategory} disabled={addingCategory} className="btn btn-primary btn-sm ml-2">
                {addingCategory ? 'Adding...' : 'Add'}
              </button>
              <button onClick={() => setShowAddCategory(false)} className="btn btn-secondary btn-sm ml-1">
                Cancel
              </button>
            </div>
          )}
        </div>

        <div>
          <button onClick={() => setShowAddSupplier(true)} className="btn btn-outline btn-sm">
            + Add New Supplier
          </button>
          {showAddSupplier && (
            <div className="mt-2">
              <input
                type="text"
                value={newSupplierName}
                onChange={(e) => setNewSupplierName(e.target.value)}
                placeholder="Supplier name"
                className="input input-sm"
              />
              <button onClick={addSupplier} disabled={addingSupplier} className="btn btn-primary btn-sm ml-2">
                {addingSupplier ? 'Adding...' : 'Add'}
              </button>
              <button onClick={() => setShowAddSupplier(false)} className="btn btn-secondary btn-sm ml-1">
                Cancel
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Search */}
      <div className="card">
        <div className="card-body">
          <input
            type="text"
            placeholder="Search by name, SKU, or barcode"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="input pl-10"
          />
        </div>
      </div>

      {/* Products Table */}
      {/* ... same as your existing code */}
      {/* Modals */}
      {showModal && (
        <ProductModal
          product={selectedProduct}
          categories={categories}
          suppliers={suppliers}
          onClose={() => setShowModal(false)}
          onSaved={() => {
            setShowModal(false);
            fetchProducts();
            toast.success('Product saved successfully');
          }}
        />
      )}

      {/* Other existing modals like DetailModal, BarcodeScanner, etc. */}
    </div>
  );
};

export default Products;
