import React, { useEffect, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { HiX } from 'react-icons/hi';
import axios from 'axios';
import toast from 'react-hot-toast';

const ProductModal = ({ product, categories, suppliers, onClose, onSaved }) => {
  const [loading, setLoading] = useState(false);
  const [generatingBarcode, setGeneratingBarcode] = useState(false);

  const {
    control,
    register,
    handleSubmit,
    formState: { errors },
    reset,
    setValue,
  } = useForm({
    defaultValues: {
      name: '',
      sku: '',
      description: '',
      unit: '',
      costPrice: '',
      sellingPrice: '',
      minStockLevel: '',
      maxStockLevel: '',
      barcode: '',
      tags: '',
      category: '',
      supplier: '',
    },
  });

  const isEditing = Boolean(product);

  // Merge fetched categories with extra static ones (outside JSX)
  const extendedCategories = [
    ...(categories ?? []),
    { _id: 'temp1', name: 'Electronics' },
    { _id: 'temp2', name: 'Books' },
    { _id: 'temp3', name: 'Headphones' },
    { _id: 'temp4', name: 'Clothes' },
  ];

  // Merge fetched suppliers with extra static ones
  const extendedSuppliers = [
    ...(suppliers ?? []),
    { _id: 'temp5', name: 'Supplier C' },
    { _id: 'temp6', name: 'Supplier D' },
  ];

  useEffect(() => {
    reset({
      name: product?.name || '',
      sku: product?.sku || '',
      description: product?.description || '',
      unit: product?.unit || '',
      costPrice: product?.costPrice || '',
      sellingPrice: product?.sellingPrice || '',
      minStockLevel: product?.minStockLevel || '',
      maxStockLevel: product?.maxStockLevel || '',
      barcode: product?.barcode || '',
      tags: product?.tags?.join(', ') || '',
      category: product?.category?._id || '',
      supplier: product?.supplier?._id || '',
    });
  }, [product, reset]);

  const generateBarcode = async () => {
    setGeneratingBarcode(true);
    try {
      const response = await axios.get('/api/barcodes/generate/new');
      setValue('barcode', response.data.barcode);
      toast.success('Barcode generated successfully');
    } catch {
      toast.error('Failed to generate barcode');
    } finally {
      setGeneratingBarcode(false);
    }
  };

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      const payload = {
        ...data,
        tags: data.tags.split(',').map((tag) => tag.trim()),
      };
      if (isEditing) {
        await axios.put(`/api/products/${product._id}`, payload);
      } else {
        await axios.post('/api/products', payload);
      }
      onSaved();
    } catch {
      toast.error('Failed to save product');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">{isEditing ? 'Edit Product' : 'Add New Product'}</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <HiX className="w-6 h-6" />
          </button>
        </div>
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          {/* Product Name & SKU */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Product Name *</label>
              <input
                type="text"
                {...register('name', { required: 'Product name is required' })}
                className="mt-1 input"
                placeholder="Enter product name"
              />
              {errors.name && <p className="mt-1 text-sm text-danger-600">{errors.name.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">SKU *</label>
              <input
                type="text"
                {...register('sku', { required: 'SKU is required' })}
                className="mt-1 input"
                placeholder="Enter SKU"
              />
              {errors.sku && <p className="mt-1 text-sm text-danger-600">{errors.sku.message}</p>}
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Description</label>
            <textarea {...register('description')} rows={3} className="mt-1 input" placeholder="Enter product description" />
          </div>

          {/* Category & Supplier */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Category *</label>
              <Controller
                name="category"
                control={control}
                rules={{ required: 'Category is required' }}
                render={({ field }) => (
                  <select {...field} className="mt-1 input">
                    <option value="">Select category</option>
                    {extendedCategories.map((cat) => (
                      <option key={cat._id} value={cat._id}>
                        {cat.name}
                      </option>
                    ))}
                  </select>
                )}
              />
              {errors.category && <p className="mt-1 text-sm text-danger-600">{errors.category.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Supplier</label>
              <Controller
                name="supplier"
                control={control}
                render={({ field }) => (
                  <select {...field} className="mt-1 input">
                    <option value="">Select supplier</option>
                    {extendedSuppliers.map((sup) => (
                      <option key={sup._id} value={sup._id}>
                        {sup.name}
                      </option>
                    ))}
                  </select>
                )}
              />
            </div>
          </div>

          {/* Remaining form fields (Unit, Cost, Prices, etc.) */}
          {/* You can add your other inputs here */}

          {/* Submit / Cancel buttons */}
          <div className="flex justify-end space-x-3">
            <button onClick={onClose} type="button" className="btn btn-secondary">Cancel</button>
            <button disabled={loading} type="submit" className="btn btn-primary">
              {loading ? (isEditing ? 'Updating...' : 'Saving...') : isEditing ? 'Update' : 'Save'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductModal;
