import React, { useState, useEffect, useCallback } from 'react';
import { HiPlus, HiSearch, HiQrcode, HiPencil, HiTrash, HiEye } from 'react-icons/hi';
import axios from 'axios';
import toast from 'react-hot-toast';
import ProductModal from './ProductModal';
import ProductDetailModal from './ProductDetailModal';
import BarcodeScanner from './BarcodeScanner';
import LoadingSpinner from '../UI/LoadingSpinner';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);

  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showScanner, setShowScanner] = useState(false);

  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // Fetch products from backend with pagination and search
  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 10,
        search: searchTerm,
      });
      const res = await axios.get(`/api/products?${params}`);
      setProducts(res.data.products || []);
      setTotalPages(res.data.totalPages || 1);
    } catch (error) {
      toast.error('Failed to fetch products');
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  }, [currentPage, searchTerm]);

  // Fetch categories from backend
  const fetchCategories = useCallback(async () => {
    try {
      const res = await axios.get('/api/categories');
      setCategories(res.data || []);
    } catch (error) {
      toast.error('Failed to load categories');
    }
  }, []);

  // Fetch suppliers from backend
  const fetchSuppliers = useCallback(async () => {
    try {
      const res = await axios.get('/api/suppliers');
      setSuppliers(res.data || []);
    } catch (error) {
      toast.error('Failed to load suppliers');
    }
  }, []);

  // Initialize fetching data on component mount & when dependencies change
  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  useEffect(() => {
    fetchCategories();
    fetchSuppliers();
  }, [fetchCategories, fetchSuppliers]);

  // Pagination controls (example)
  const goToNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };
  const goToPrevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  // Handlers for product modals and actions
  const handleAddProduct = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  const handleEditProduct = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleViewProduct = (product) => {
    setSelectedProduct(product);
    setShowDetailModal(true);
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await axios.delete(`/api/products/${id}`);
      toast.success('Product deleted');
      fetchProducts();
    } catch (error) {
      toast.error('Failed to delete product');
      console.error('Error deleting product:', error);
    }
  };

  const handleProductSaved = () => {
    setShowModal(false);
    fetchProducts();
    toast.success('Product saved');
  };

  const handleBarcodeScan = async (barcode) => {
    try {
      const res = await axios.post('/api/barcodes/scan', { barcode });
      setSelectedProduct(res.data);
      setShowDetailModal(true);
      setShowScanner(false);
      toast.success('Product found');
    } catch {
      toast.error('Product not found');
    }
  };

  // Stock status badge helper
  const getStockStatusBadge = (product) => {
    if (product.currentStock <= product.minStockLevel) {
      return <span className="badge-danger">Low Stock</span>;
    }
    if (product.maxStockLevel && product.currentStock >= product.maxStockLevel) {
      return <span className="badge-warning">Over Stock</span>;
    }
    return <span className="badge-success">Normal</span>;
  };

  if (loading) {
    return <LoadingSpinner size="lg" className="h-64" />;
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Products</h1>
          <p className="text-gray-600">Manage your product inventory</p>
        </div>
        <div className="mt-4 sm:mt-0 flex space-x-3">
          <button onClick={() => setShowScanner(true)} className="btn btn-outline">
            <HiQrcode className="w-4 h-4 mr-2" />
            Scan Barcode
          </button>
          <button onClick={handleAddProduct} className="btn btn-primary">
            <HiPlus className="w-4 h-4 mr-2" />
            Add Product
          </button>
        </div>
      </div>

      {/* Search */}
      <div className="card">
        <div className="card-body">
          <div className="relative">
            <HiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search products by name, SKU, or barcode..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input pl-10"
            />
          </div>
        </div>
      </div>

      {/* Products Table */}
      <div className="card">
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table w-full">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>SKU</th>
                  <th>Category</th>
                  <th>Stock</th>
                  <th>Price</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="text-center text-gray-500 py-6">
                      No products found.
                    </td>
                  </tr>
                ) : (
                  products.map((product) => (
                    <tr key={product._id}>
                      <td>{product.name}</td>
                      <td>{product.sku}</td>
                      <td>{product.category?.name || '—'}</td>
                      <td>{product.currentStock} {product.unit}</td>
                      <td>${product.sellingPrice}</td>
                      <td>{getStockStatusBadge(product)}</td>
                      <td className="flex space-x-2">
                        <button
                          onClick={() => handleViewProduct(product)}
                          title="View"
                          className="text-blue-600 hover:underline"
                        >
                          <HiEye />
                        </button>
                        <button
                          onClick={() => handleEditProduct(product)}
                          title="Edit"
                          className="text-green-600 hover:underline"
                        >
                          <HiPencil />
                        </button>
                        <button
                          onClick={() => handleDeleteProduct(product._id)}
                          title="Delete"
                          className="text-red-600 hover:underline"
                        >
                          <HiTrash />
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
            {/* Optional Pagination Controls */}
            <div className="mt-4 flex justify-between">
              <button
                disabled={currentPage === 1}
                onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
                className="btn btn-secondary"
              >
                Previous
              </button>
              <span className="text-sm text-gray-700">
                Page {currentPage} of {totalPages}
              </span>
              <button
                disabled={currentPage === totalPages}
                onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
                className="btn btn-secondary"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Modals */}
      {showModal && (
        <ProductModal
          product={selectedProduct}
          categories={categories}
          suppliers={suppliers}
          onClose={() => setShowModal(false)}
          onSaved={handleProductSaved}
        />
      )}

      {showDetailModal && (
        <ProductDetailModal product={selectedProduct} onClose={() => setShowDetailModal(false)} />
      )}

      {showScanner && (
        <BarcodeScanner onClose={() => setShowScanner(false)} onScan={handleBarcodeScan} />
      )}
    </div>
  );
};

export default Products;
