import React, { useState, useEffect } from "react";

function ProductForm() {
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSupplier, setSelectedSupplier] = useState("");

  useEffect(() => {
    setCategories([
      { _id: "1", name: "Electronics" },
      { _id: "2", name: "Clothing" },
    ]);
    setSuppliers([
      { _id: "1", name: "ABC Suppliers" },
      { _id: "2", name: "XYZ Traders" },
    ]);
  }, []);

  return (
    <div style={{ padding: "20px", position: "relative", zIndex: 1 }}>
      <h2>Add Product</h2>

      <div style={{ marginBottom: "20px" }}>
        <label htmlFor="category-select">Category *</label>
        <br />
        <select
          id="category-select"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          style={{
            width: "250px",
            height: "35px",
            fontSize: "16px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            padding: "4px 8px",
            position: "relative",
            zIndex: 1000, // high z-index to avoid overlap
            backgroundColor: "white",
            appearance: "none", // reset default styles for consistency
            WebkitAppearance: "none",
            MozAppearance: "none",
          }}
        >
          <option value="">-- Select Category --</option>
          <option value="">-- Electronics --</option>
          <option value="">-- Clothing --</option>
          <option value="">-- Grocery --</option>
          <option value="">-- Home & Kitchen --</option>
          <option value="">-- Beauty & Health --</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: "20px" }}>
        <label htmlFor="supplier-select">Supplier</label>
        <br />
        <select
          id="supplier-select"
          value={selectedSupplier}
          onChange={(e) => setSelectedSupplier(e.target.value)}
          style={{
            width: "250px",
            height: "35px",
            fontSize: "16px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            padding: "4px 8px",
            backgroundColor: "white",
            appearance: "none",
            WebkitAppearance: "none",
            MozAppearance: "none",
          }}
        >
          <option value="">-- Select Supplier --</option>
          {suppliers.map((sup) => (
            <option key={sup._id} value={sup._id}>
              {sup.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <p>
          Selected Category: {selectedCategory ? selectedCategory : "None"}
        </p>
        <p>Selected Supplier: {selectedSupplier ? selectedSupplier : "None"}</p>
      </div>
    </div>
  );
}

export default ProductForm;
