import React, { useState, useEffect } from 'react';
import { HiDownload, HiChartBar, HiExclamation, HiCheckCircle } from 'react-icons/hi';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import LoadingSpinner from '../UI/LoadingSpinner';

const Reports = () => {
  const [loading, setLoading] = useState(true);
  const [inventorySummary, setInventorySummary] = useState(null);
  const [transactionSummary, setTransactionSummary] = useState(null);
  const [lowStockReport, setLowStockReport] = useState([]);
  const [overStockReport, setOverStockReport] = useState([]);
  const [dateRange, setDateRange] = useState({
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0],
    endDate: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    fetchReports();
  }, [dateRange]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const [inventoryRes, transactionRes, lowStockRes, overStockRes] = await Promise.all([
        axios.get('/api/reports/inventory-summary'),
        axios.get(`/api/reports/transaction-summary?startDate=${dateRange.startDate}&endDate=${dateRange.endDate}`),
        axios.get('/api/reports/low-stock-report'),
        axios.get('/api/reports/overstock-report')
      ]);

      setInventorySummary(inventoryRes.data);
      setTransactionSummary(transactionRes.data);
      setLowStockReport(lowStockRes.data);
       setOverStockReport(overStockRes.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const exportReport = (data, filename) => {
    const csvContent = convertToCSV(data);
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', filename);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const convertToCSV = (data) => {
    if (!data || data.length === 0) return '';
    
    const headers = Object.keys(data[0]);
    const csvRows = [headers.join(',')];
    
    for (const row of data) {
      const values = headers.map(header => {
        const value = row[header];
        return typeof value === 'string' ? `"${value}"` : value;
      });
      csvRows.push(values.join(','));
    }

     return csvRows.join('\n');
  };

  if (loading) {
    return <LoadingSpinner size="lg" className="h-64" />;
  }

  const chartColors = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#06B6D4'];

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Reports & Analytics</h1>
        <p className="text-gray-600">Comprehensive inventory insights and analytics</p>
      </div>

      {/* Date Range Filter */}
      <div className="card">
        <div className="card-body">
          <div className="flex items-center space-x-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Start Date</label>
              <input
                type="date"
                value={dateRange.startDate}
                onChange={(e) => setDateRange({ ...dateRange, startDate: e.target.value })}
                className="mt-1 input"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">End Date</label>
              <input
                type="date"
                 value={dateRange.endDate}
                onChange={(e) => setDateRange({ ...dateRange, endDate: e.target.value })}
                className="mt-1 input"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Inventory Summary */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900">Inventory Summary</h3>
          </div>
          <div className="card-body">
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="text-center">
                  <p className="text-2xl font-bold text-primary-600">{inventorySummary?.totalProducts || 0}</p>
                  <p className="text-sm text-gray-500">Total Products</p>
                </div>
                <div className="text-center">
                  <p className="text-2xl font-bold text-success-600">${inventorySummary?.totalValue || '0.00'}</p>
                  <p className="text-sm text-gray-500">Total Value</p>
                </div>
              </div>
              
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={[
                         { name: 'Products', value: inventorySummary?.totalProducts || 0 },
                        { name: 'Low Stock', value: inventorySummary?.lowStockCount || 0 },
                        { name: 'Out of Stock', value: inventorySummary?.outOfStockCount || 0 }
                      ]}
                      cx="50%"
                      cy="50%"
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                    >
                      {chartColors.map((color, index) => (
                        <Cell key={`cell-${index}`} fill={color} />
                      ))}
                    </Pie>
                    <Tooltip />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        </div>

        {/* Transaction Summary */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-medium text-gray-900">Transaction Summary</h3>
          </div>
          <div className="card-body">
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="text-center">
                  <p className="text-2xl font-bold text-primary-600">{transactionSummary?.totalTransactions || 0}</p>
                  <p className="text-sm text-gray-500">Total Transactions</p>
                </div>
                 <div className="text-center">
                  <p className="text-2xl font-bold text-success-600">${transactionSummary?.totalValue || '0.00'}</p>
                  <p className="text-sm text-gray-500">Total Value</p>
                </div>
              </div>
              
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={[
                    { name: 'Stock In', value: transactionSummary?.totalIn || 0 },
                    { name: 'Stock Out', value: transactionSummary?.totalOut || 0 }
                  ]}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="value" fill="#3B82F6" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Low Stock Report */}
      <div className="card">
        <div className="card-header">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-medium text-gray-900">Low Stock Report</h3>
            <button
              onClick={() => exportReport(lowStockReport, 'low-stock-report.csv')}
              className="btn btn-outline"
                >
              <HiDownload className="w-4 h-4 mr-2" />
              Export CSV
            </button>
          </div>
        </div>
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>SKU</th>
                  <th>Current Stock</th>
                  <th>Min Level</th>
                  <th>Category</th>
                  <th>Supplier</th>
                  <th>Stock Value</th>
                </tr>
              </thead>
              <tbody>
                {lowStockReport.map((product) => (
                  <tr key={product.id}>
                    <td className="font-medium">{product.name}</td>
                    <td className="font-mono">{product.sku}</td>
                    <td>
                      <span className="badge-danger">{product.currentStock} {product.unit}</span>
                    </td>
                    <td>{product.minStockLevel}</td>
                    <td>{product.category}</td>
                    <td>{product.supplier}</td>
                    <td>${product.stockValue}</td>
                  </tr>
                ))}
                  </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Over Stock Report */}
      <div className="card">
        <div className="card-header">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-medium text-gray-900">Over Stock Report</h3>
            <button
              onClick={() => exportReport(overStockReport, 'overstock-report.csv')}
              className="btn btn-outline"
            >
              <HiDownload className="w-4 h-4 mr-2" />
              Export CSV
            </button>
          </div>
        </div>
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>SKU</th>
                  <th>Current Stock</th>
                  <th>Max Level</th>
                  <th>Category</th>
                  <th>Supplier</th>
                  <th>Stock Value</th>
                </tr>
              </thead>
               <tbody>
                {overStockReport.map((product) => (
                  <tr key={product.id}>
                    <td className="font-medium">{product.name}</td>
                    <td className="font-mono">{product.sku}</td>
                    <td>
                      <span className="badge-warning">{product.currentStock} {product.unit}</span>
                    </td>
                    <td>{product.maxStockLevel}</td>
                    <td>{product.category}</td>
                    <td>{product.supplier}</td>
                    <td>${product.stockValue}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reports;
