import React, { useState, useEffect, useCallback } from "react";
import { HiPlus, HiSearch, HiQrcode, HiPencil, HiTrash, HiEye } from "react-icons/hi";
import axios from "axios";
import toast from "react-hot-toast";
import ProductModal from "./ProductModal";
import ProductDetailModal from "./ProductDetailModal";
import BarcodeScanner from "./BarcodeScanner";
import LoadingSpinner from "../UI/LoadingSpinner";

const Products = () => {
  // Core data
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // UI state
  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showScanner, setShowScanner] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Filters & pagination
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // Dropdown data
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);

  // Helper: normalize different API shapes to an array
  const normalizeArray = (raw) => {
    if (Array.isArray(raw)) return raw;
    if (!raw) return [];
    if (raw.data && Array.isArray(raw.data)) return raw.data;
    if (raw.categories && Array.isArray(raw.categories)) return raw.categories;
    if (raw.suppliers && Array.isArray(raw.suppliers)) return raw.suppliers;
    if (raw.items && Array.isArray(raw.items)) return raw.items;
    return [];
  };

  // Fetch products (with page/search)
  const fetchProducts = useCallback(
    async (page = currentPage, search = searchTerm) => {
      setLoading(true);
      try {
        const limit = 10;
        const params = new URLSearchParams({
          page,
          limit,
          search,
        });

        const res = await axios.get(`/api/products?${params.toString()}`);

        // Support multiple response shapes:
        // - { products: [...], totalPages }
        // - { data: [...] }
        // - [...array...]
        // - { items: [...], total }
        let list = [];
        if (Array.isArray(res.data)) {
          list = res.data;
        } else if (res.data.products && Array.isArray(res.data.products)) {
          list = res.data.products;
        } else if (res.data.data && Array.isArray(res.data.data)) {
          list = res.data.data;
        } else if (res.data.items && Array.isArray(res.data.items)) {
          list = res.data.items;
        } else {
          list = [];
        }

        setProducts(list);

        // Determine total pages from server response if available
        if (res.data.totalPages) {
          setTotalPages(res.data.totalPages);
        } else if (res.data.total || res.data.totalItems || res.data.totalCount) {
          const total = res.data.total || res.data.totalItems || res.data.totalCount;
          setTotalPages(Math.max(1, Math.ceil(total / 10)));
        } else {
          // fallback: if server doesn't provide total, keep 1 or derive from length
          setTotalPages(Math.max(1, Math.ceil(list.length / 10)));
        }
      } catch (error) {
        toast.error("Failed to fetch products");
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false);
      }
    },
    [currentPage, searchTerm]
  );

  // Fetch categories
  const fetchCategories = useCallback(async () => {
    try {
      const res = await axios.get("/api/categories");
      const list = normalizeArray(res.data);
      setCategories(list);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  }, []);

  // Fetch suppliers
  const fetchSuppliers = useCallback(async () => {
    try {
      const res = await axios.get("/api/suppliers");
      const list = normalizeArray(res.data);
      setSuppliers(list);
    } catch (error) {
      console.error("Error fetching suppliers:", error);
    }
  }, []);

  // Initial load
  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchSuppliers();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Re-fetch when page or search changes
  useEffect(() => {
    fetchProducts(currentPage, searchTerm);
  }, [currentPage, searchTerm, fetchProducts]);

  // When user types a new search, reset to page 1
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm]);

  // Handlers
  const handleAddProduct = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  const handleEditProduct = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleViewProduct = (product) => {
    setSelectedProduct(product);
    setShowDetailModal(true);
  };

  const handleDeleteProduct = async (productId) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;
    try {
      await axios.delete(`/api/products/${productId}`);
      toast.success("Product deleted");
      // If last item on page removed, go to previous page if possible
      const remaining = products.length - 1;
      if (remaining === 0 && currentPage > 1) {
        setCurrentPage((p) => p - 1);
      } else {
        fetchProducts(currentPage, searchTerm);
      }
    } catch (error) {
      toast.error("Failed to delete product");
      console.error("Error deleting product:", error);
    }
  };

  const handleProductSaved = () => {
    setShowModal(false);
    setSelectedProduct(null);
    fetchProducts(currentPage, searchTerm);
    toast.success("Product saved");
  };

  const handleBarcodeScan = async (barcode) => {
    try {
      const response = await axios.post("/api/barcodes/scan", { barcode });
      setSelectedProduct(response.data);
      setShowDetailModal(true);
      setShowScanner(false);
      toast.success("Product found");
    } catch (error) {
      toast.error("Product not found");
      console.error("Error scanning barcode:", error);
    }
  };

  const getStockStatusBadge = (product) => {
    if (product.currentStock <= (product.minStockLevel ?? 0)) {
      return <span className="badge-danger">Low Stock</span>;
    } else if (product.maxStockLevel && product.currentStock >= product.maxStockLevel) {
      return <span className="badge-warning">Over Stock</span>;
    }
    return <span className="badge-success">Normal</span>;
  };

  if (loading) return <LoadingSpinner size="lg" className="h-64" />;

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Products</h1>
          <p className="text-gray-600">Manage your product inventory</p>
        </div>
        <div className="mt-4 sm:mt-0 flex space-x-3">
          <button onClick={() => setShowScanner(true)} className="btn btn-outline">
            <HiQrcode className="w-4 h-4 mr-2" />
            Scan Barcode
          </button>
          <button onClick={handleAddProduct} className="btn btn-primary">
            <HiPlus className="w-4 h-4 mr-2" />
            Add Product
          </button>
        </div>
      </div>

      {/* Search */}
      <div className="card">
        <div className="card-body">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <HiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                <input
                  type="text"
                  placeholder="Search products by name, SKU, or barcode..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="input pl-10"
                />
              </div>
            </div>

            {/* optional quick filter dropdowns using loaded categories/suppliers */}
            <div className="flex gap-2">
              <select
                className="input"
                onChange={(e) => {
                  // quick filter by category id
                  const catId = e.target.value;
                  if (!catId) return fetchProducts(1, searchTerm);
                  // Trigger a simple search by category (assumes backend supports ?category=)
                  fetchProducts(1, `${searchTerm}&category=${catId}`);
                }}
                defaultValue=""
              >
                <option value="">All Categories</option>
                {categories.map((c) => (
                  <option key={c._id} value={c._id}>
                    {c.name}
                  </option>
                ))}
              </select>

              <select
                className="input"
                onChange={(e) => {
                  const supId = e.target.value;
                  if (!supId) return fetchProducts(1, searchTerm);
                  fetchProducts(1, `${searchTerm}&supplier=${supId}`);
                }}
                defaultValue=""
              >
                <option value="">All Suppliers</option>
                {suppliers.map((s) => (
                  <option key={s._id} value={s._id}>
                    {s.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Products Table */}
      <div className="card">
        <div className="card-body">
          <div className="overflow-x-auto">
            <table className="table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>SKU</th>
                  <th>Category</th>
                  <th>Stock</th>
                  <th>Price</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.length === 0 && (
                  <tr>
                    <td colSpan={7} className="text-center text-gray-500 py-6">
                      No products found.
                    </td>
                  </tr>
                )}
                {products.map((product) => (
                  <tr key={product._id}>
                    <td>
                      <div>
                        <p className="font-medium text-gray-900">{product.name}</p>
                        <p className="text-sm text-gray-500">{product.description}</p>
                      </div>
                    </td>
                    <td>
                      <span className="font-mono text-sm bg-gray-100 px-2 py-1 rounded">
                        {product.sku}
                      </span>
                    </td>
                    <td>
                      <span
                        className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium"
                        style={{
                          backgroundColor: `${product.category?.color || "#555"}20`,
                          color: product.category?.color || "#555",
                        }}
                      >
                        {product.category?.name || "—"}
                      </span>
                    </td>
                    <td>
                      <div>
                        <p className="font-medium">
                          {product.currentStock ?? 0} {product.unit || "piece"}
                        </p>
                        <p className="text-xs text-gray-500">
                          Min: {product.minStockLevel ?? "—"} | Max:{" "}
                          {product.maxStockLevel ?? "—"}
                        </p>
                      </div>
                    </td>
                    <td>
                      <div>
                        <p className="font-medium">${product.sellingPrice ?? 0}</p>
                        <p className="text-xs text-gray-500">
                          Cost: ${product.costPrice ?? 0}
                        </p>
                      </div>
                    </td>
                    <td>{getStockStatusBadge(product)}</td>
                    <td>
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleViewProduct(product)}
                          className="p-1 text-gray-400 hover:text-gray-600"
                          title="View Details"
                        >
                          <HiEye className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleEditProduct(product)}
                          className="p-1 text-gray-400 hover:text-blue-600"
                          title="Edit Product"
                        >
                          <HiPencil className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleDeleteProduct(product._id)}
                          className="p-1 text-gray-400 hover:text-red-600"
                          title="Delete Product"
                        >
                          <HiTrash className="w-4 h-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          <div className="mt-4 flex items-center justify-between">
            <div className="text-sm text-gray-600">
              Page {currentPage} of {totalPages}
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() => setCurrentPage((p) => Math.max(1, p - 1))}
                disabled={currentPage === 1}
                className="btn btn-outline"
              >
                Previous
              </button>
              <button
                onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))}
                disabled={currentPage === totalPages}
                className="btn btn-outline"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Modals */}
      {showModal && (
        <ProductModal
          product={selectedProduct}
          categories={categories}
          suppliers={suppliers}
          onClose={() => setShowModal(false)}
          onSaved={handleProductSaved}
        />
      )}

      {showDetailModal && (
        <ProductDetailModal product={selectedProduct} onClose={() => setShowDetailModal(false)} />
      )}

      {showScanner && (
        <BarcodeScanner
          onClose={() => setShowScanner(false)}
          onScan={(value) => handleBarcodeScan(value)}
        />
      )}
    </div>
  );
};

export default Products;
