import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import ProductModal from './ProductModal';
import LoadingSpinner from '../UI/LoadingSpinner';
import { HiPlus, HiQrcode, HiSearch } from 'react-icons/hi';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);

  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // Fetch products including pagination and search
  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 10,
        search: searchTerm,
      });

      const response = await axios.get(`/api/products?${params.toString()}`);
      setProducts(response.data.products);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      toast.error('Failed to fetch products');
    } finally {
      setLoading(false);
    }
  };

  // Fetch categories from backend
  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Failed to fetch categories', error);
    }
  };

  // Fetch suppliers from backend
  const fetchSuppliers = async () => {
    try {
      const response = await axios.get('/api/suppliers');
      setSuppliers(response.data);
    } catch (error) {
      console.error('Failed to fetch suppliers', error);
    }
  };

  // Initial fetch on mount and when page or search term changes
  useEffect(() => {
    fetchProducts();
  }, [currentPage, searchTerm]);

  useEffect(() => {
    fetchCategories();
    fetchSuppliers();
  }, []);

  const openAddProductModal = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  const openEditProductModal = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleProductSaved = () => {
    setShowModal(false);
    fetchProducts();
    toast.success('Product saved successfully');
  };

  if (loading) return <LoadingSpinner size="lg" className="h-64" />;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Products</h1>
        <button onClick={openAddProductModal} className="btn btn-primary flex items-center space-x-2">
          <HiPlus className="w-5 h-5" />
          <span>Add Product</span>
        </button>
      </div>

      <div className="mb-4">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="input w-full max-w-md"
        />
      </div>

      <table className="table w-full">
        <thead>
          <tr>
            <th>Name</th>
            <th>SKU</th>
            <th>Category</th>
            <th>Supplier</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.length === 0 && (
            <tr>
              <td colSpan="7" className="text-center py-10">
                No products found.
              </td>
            </tr>
          )}
          {products.map((prod) => (
            <tr key={prod._id}>
              <td>{prod.name}</td>
              <td>{prod.sku}</td>
              <td>{prod.category?.name || '-'}</td>
              <td>{prod.supplier?.name || '-'}</td>
              <td>${prod.sellingPrice.toFixed(2)}</td>
              <td>{prod.currentStock} {prod.unit}</td>
              <td>
                <button onClick={() => openEditProductModal(prod)} className="btn btn-sm btn-outline">
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {totalPages > 1 && (
        <div className="flex justify-center space-x-3 mt-4">
          <button
            className="btn btn-outline"
            disabled={currentPage === 1}
            onClick={() => setCurrentPage((p) => Math.max(1, p - 1))}
          >
            Previous
          </button>
          <span className="px-3 py-1 bg-gray-200 rounded">{`Page ${currentPage} of ${totalPages}`}</span>
          <button
            className="btn btn-outline"
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage((p) => Math.min(totalPages, p + 1))}
          >
            Next
          </button>
        </div>
      )}

      {showModal && (
        <ProductModal
          product={selectedProduct}
          categories={categories}
          suppliers={suppliers}
          onClose={() => setShowModal(false)}
          onSaved={handleProductSaved}
        />
      )}
    </div>
  );
};

export default Products;
