import React, { useState, useEffect } from 'react';
import { HiPlus, HiSearch, HiQrcode, HiPencil, HiTrash, HiEye } from 'react-icons/hi';
import axios from 'axios';
import toast from 'react-hot-toast';
import ProductModal from './ProductModal';
import ProductDetailModal from './ProductDetailModal';
import BarcodeScanner from './BarcodeScanner';
import LoadingSpinner from '../UI/LoadingSpinner';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showScanner, setShowScanner] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);

  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchSuppliers();
  }, [currentPage, searchTerm]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 10,
        search: searchTerm,
      });
      const response = await axios.get(`/api/products?${params}`);
      setProducts(response.data.products ?? []);
      setTotalPages(response.data.totalPages ?? 1);
    } catch (error) {
      toast.error('Failed to fetch products');
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data ?? []);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchSuppliers = async () => {
    try {
      const response = await axios.get('/api/suppliers');
      setSuppliers(response.data ?? []);
    } catch (error) {
      console.error('Error fetching suppliers:', error);
    }
  };

  const handleAddProduct = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  const handleEditProduct = (product) => {
    setSelectedProduct(product ?? {});
    setShowModal(true);
  };

  const handleViewProduct = (product) => {
    setSelectedProduct(product ?? {});
    setShowDetailModal(true);
  };

  const handleDeleteProduct = async (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await axios.delete(`/api/products/${productId}`);
        toast.success('Product deleted successfully');
        fetchProducts();
      } catch (error) {
        toast.error('Failed to delete product');
        console.error('Error deleting product:', error);
      }
    }
  };

  const handleProductSaved = () => {
    setShowModal(false);
    fetchProducts();
    toast.success('Product saved successfully');
  };

  const handleBarcodeScan = async (barcode) => {
    try {
      const response = await axios.post('/api/barcodes/scan', { barcode });
      setSelectedProduct(response.data ?? {});
      setShowDetailModal(true);
      setShowScanner(false);
      toast.success('Product found!');
    } catch (error) {
      toast.error('Product not found');
      console.error('Error scanning barcode:', error);
    }
  };

  const getStockStatusBadge = (product) => {
    if (!product) return null;
    if ((product.currentStock ?? 0) <= (product.minStockLevel ?? 0)) {
      return <span className="badge-danger">Low Stock</span>;
    } else if ((product.currentStock ?? 0) >= (product.maxStockLevel ?? Infinity)) {
      return <span className="badge-warning">Over Stock</span>;
    }
    return <span className="badge-success">Normal</span>;
  };

  if (loading) return <LoadingSpinner size="lg" className="h-64" />;

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Products</h1>
          <p className="text-gray-600">Manage your product inventory</p>
        </div>
        <div className="mt-4 sm:mt-0 flex space-x-3">
          <button
            onClick={() => setShowScanner(true)}
            className="btn btn-outline"
          >
            <HiQrcode className="w-4 h-4 mr-2" />
            Scan Barcode
          </button>
          <button
            onClick={handleAddProduct}
            className="btn btn-primary"
          >
            <HiPlus className="w-4 h-4 mr-2" />
            Add Product
          </button>
        </div>
      </div>

      {/* Search */}
      <div className="card">
        <div className="card-body">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1 relative">
              <HiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search products by name, SKU, or barcode..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input pl-10"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Products Table */}
      <div className="card">
        <div className="card-body overflow-x-auto">
          <table className="table w-full">
            <thead>
              <tr>
                <th>Product</th>
                <th>SKU</th>
                <th>Category</th>
                <th>Stock</th>
                <th>Price</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {(products ?? []).map((product) => (
                <tr key={product._id}>
                  <td>
                    <div>
                      <p className="font-medium text-gray-900">{product.name}</p>
                      <p className="text-sm text-gray-500">{product.description}</p>
                    </div>
                  </td>
                  <td>
                    <span className="font-mono text-sm bg-gray-100 px-2 py-1 rounded">{product.sku}</span>
                  </td>
                  <td>
                    <span
                      className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium"
                      style={{
                        backgroundColor: `${product.category?.color ?? '#ccc'}20`,
                        color: product.category?.color ?? '#333',
                      }}
                    >
                      {product.category?.name ?? 'N/A'}
                    </span>
                  </td>
                  <td>
                    <div>
                      <p className="font-medium">{product.currentStock ?? 0} {product.unit ?? ''}</p>
                      <p className="text-xs text-gray-500">
                        Min: {product.minStockLevel ?? 0} | Max: {product.maxStockLevel ?? 'N/A'}
                      </p>
                    </div>
                  </td>
                  <td>
                    <div>
                      <p className="font-medium">${product.sellingPrice ?? 0}</p>
                      <p className="text-xs text-gray-500">Cost: ${product.costPrice ?? 0}</p>
                    </div>
                  </td>
                  <td>{getStockStatusBadge(product)}</td>
                  <td>
                    <div className="flex space-x-2">
                      <button onClick={() => handleViewProduct(product)} className="p-1 text-gray-400 hover:text-gray-600" title="View Details">
                        <HiEye className="w-4 h-4" />
                      </button>
                      <button onClick={() => handleEditProduct(product)} className="p-1 text-gray-400 hover:text-blue-600" title="Edit Product">
                        <HiPencil className="w-4 h-4" />
                      </button>
                      <button onClick={() => handleDeleteProduct(product._id)} className="p-1 text-gray-400 hover:text-red-600" title="Delete Product">
                        <HiTrash className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="mt-6 flex items-center justify-between">
              <div className="text-sm text-gray-700">Page {currentPage} of {totalPages}</div>
              <div className="flex space-x-2">
                <button
                  onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                  disabled={currentPage === 1}
                  className="btn btn-outline disabled:opacity-50"
                >
                  Previous
                </button>
                <button
                  onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                  disabled={currentPage === totalPages}
                  className="btn btn-outline disabled:opacity-50"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Modals */}
      {showModal && (
        <ProductModal
          product={selectedProduct}
          categories={categories}
          suppliers={suppliers}
          onClose={() => setShowModal(false)}
          onSaved={handleProductSaved}
        />
      )}

      {showDetailModal && (
        <ProductDetailModal
          product={selectedProduct}
          onClose={() => setShowDetailModal(false)}
        />
      )}

      {showScanner && (
        <BarcodeScanner
          onClose={() => setShowScanner(false)}
          onScan={handleBarcodeScan}
        />
      )}
    </div>
  );
};

export default Products;
