import React, { useState, useEffect } from "react";
import axios from "axios"; // Make sure axios is installed

function ProductForm() {
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);

  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSupplier, setSelectedSupplier] = useState("");

  // Fetch categories and suppliers from backend
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await axios.get("/api/categories"); // Replace with your actual endpoint
        setCategories(res.data);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };

    const fetchSuppliers = async () => {
      try {
        const res = await axios.get("/api/suppliers"); // Replace with your actual endpoint
        setSuppliers(res.data);
      } catch (error) {
        console.error("Error fetching suppliers:", error);
      }
    };

    fetchCategories();
    fetchSuppliers();
  }, []);

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
      <h2>Add Product</h2>

      {/* Category Dropdown */}
      <div style={{ marginBottom: "15px" }}>
        <label style={{ display: "block", marginBottom: "5px" }}>
          Category *
        </label>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          style={{ width: "100%", padding: "8px" }}
        >
          <option value="">-- Select Category --</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat._id}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      {/* Supplier Dropdown */}
      <div style={{ marginBottom: "15px" }}>
        <label style={{ display: "block", marginBottom: "5px" }}>
          Supplier
        </label>
        <select
          value={selectedSupplier}
          onChange={(e) => setSelectedSupplier(e.target.value)}
          style={{ width: "100%", padding: "8px" }}
        >
          <option value="">-- Select Supplier --</option>
          {suppliers.map((sup) => (
            <option key={sup._id} value={sup._id}>
              {sup.name}
            </option>
          ))}
        </select>
      </div>

      {/* Show Selected Values */}
      <div style={{ marginTop: "10px" }}>
        <p>
          Selected Category:{" "}
          {categories.find((c) => String(c._id) === String(selectedCategory))
            ?.name || "None"}
        </p>
        <p>
          Selected Supplier:{" "}
          {suppliers.find((s) => String(s._id) === String(selectedSupplier))
            ?.name || "None"}
        </p>
      </div>
    </div>
  );
}

export default ProductForm;
