import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductModal from './ProductModal';

const Products = () => {
  const [categories, setCategories] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const categoriesRes = await axios.get('/api/categories');
        setCategories(categoriesRes.data);

        const suppliersRes = await axios.get('/api/suppliers');
        setSuppliers(suppliersRes.data);
      } catch (err) {
        console.error('Failed to fetch categories or suppliers', err);
      }
    };

    fetchData();
  }, []);

  const openAddProductModal = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  return (
    <div>
      {/* Your other UI elements here */}
      <button onClick={openAddProductModal}>Add Product</button>

      {showModal && (
        <ProductModal
          product={selectedProduct}
          categories={categories}
          suppliers={suppliers}
          onClose={() => setShowModal(false)}
          onSaved={() => {
            setShowModal(false);
            // refresh your product list here if needed
          }}
        />
      )}
    </div>
  );
};

export default Products;
