import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { HiX } from 'react-icons/hi';
import axios from 'axios';

const SupplierModal = ({ supplier, onClose, onSaved }) => {
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch
  } = useForm();

  const isEditing = !!supplier;

  useEffect(() => {
    fetchCategories();
    if (supplier) {
      Object.keys(supplier).forEach(key => {
        if (key !== '_id' && key !== '__v' && key !== 'createdAt' && key !== 'updatedAt') {
          setValue(key, supplier[key]);
        }
      });
    }
  }, [supplier, setValue]);

  const fetchCategories = async () => {
    try {
      const response = await axios.get('/api/categories');
      setCategories(response.data);
      } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      if (isEditing) {
        await axios.put(`/api/suppliers/${supplier._id}`, data);
      } else {
        await axios.post('/api/suppliers', data);
      }
      onSaved();
    } catch (error) {
      const message = error.response?.data?.msg || 'Failed to save supplier';
      console.error('Error saving supplier:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            {isEditing ? 'Edit Supplier' : 'Add New Supplier'}
          </h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <HiX className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Supplier Name *</label>
              <input
                type="text"
                {...register('name', { required: 'Supplier name is required' })}
                className="mt-1 input"
                placeholder="Enter supplier name"
              />
              {errors.name && (
                <p className="mt-1 text-sm text-danger-600">{errors.name.message}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">Supplier Code *</label>
              <input
                type="text"
                {...register('code', { required: 'Supplier code is required' })}
                className="mt-1 input"
                placeholder="Enter supplier code"
              />
              {errors.code && (
                <p className="mt-1 text-sm text-danger-600">{errors.code.message}</p>
              )}
            </div>
          </div>


             {/* Contact Person */}
          <div>
            <h4 className="text-sm font-medium text-gray-700 mb-2">Contact Person</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-xs text-gray-500">Name</label>
                <input
                  type="text"
                  {...register('contactPerson.name')}
                  className="mt-1 input"
                  placeholder="Contact person name"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">Position</label>
                <input
                  type="text"
                  {...register('contactPerson.position')}
                  className="mt-1 input"
                  placeholder="Position/Title"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">Email</label>
                <input
                  type="email"
                  {...register('contactPerson.email')}
                  className="mt-1 input"
                  placeholder="contact@supplier.com"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">Phone</label>
                <input
                 type="text"
                  {...register('contactPerson.phone')}
                  className="mt-1 input"
                  placeholder="Phone number"
                />
              </div>
            </div>
          </div>

          {/* Address */}
          <div>
            <h4 className="text-sm font-medium text-gray-700 mb-2">Address</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="md:col-span-2">
                <label className="block text-xs text-gray-500">Street</label>
                <input
                  type="text"
                  {...register('address.street')}
                  className="mt-1 input"
                  placeholder="Street address"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">City</label>
                <input
                  type="text"
                  {...register('address.city')}
                  className="mt-1 input"
                  placeholder="City"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">State</label>
                 <input
                  type="text"
                  {...register('address.state')}
                  className="mt-1 input"
                  placeholder="State/Province"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">ZIP Code</label>
                <input
                  type="text"
                  {...register('address.zipCode')}
                  className="mt-1 input"
                  placeholder="ZIP/Postal code"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-500">Country</label>
                <input
                  type="text"
                  {...register('address.country')}
                  className="mt-1 input"
                  placeholder="Country"
                />
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Phone</label>
              <input
                type="text"
                {...register('phone')}
                  className="mt-1 input"
                placeholder="Main phone number"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">Email</label>
              <input
                type="email"
                {...register('email')}
                className="mt-1 input"
                placeholder="Main email address"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Website</label>
            <input
              type="url"
              {...register('website')}
              className="mt-1 input"
              placeholder="https://supplier.com"
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Tax ID</label>
              <input
                type="text"
                {...register('taxId')}
                className="mt-1 input"
                placeholder="Tax identification number"
                  />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">Payment Terms</label>
              <select
                {...register('paymentTerms')}
                className="mt-1 input"
              >
                <option value="net30">Net 30</option>
                <option value="net60">Net 60</option>
                <option value="net90">Net 90</option>
                <option value="immediate">Immediate</option>
                <option value="custom">Custom</option>
              </select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Credit Limit</label>
              <input
                type="number"
                step="0.01"
                {...register('creditLimit', { 
                  min: { value: 0, message: 'Credit limit cannot be negative' }
                })}
                className="mt-1 input"
                placeholder="0.00"
              />
              {errors.creditLimit && (
                <p className="mt-1 text-sm text-danger-600">{errors.creditLimit.message}</p>
              )}
            </div>

             <div>
              <label className="block text-sm font-medium text-gray-700">Rating</label>
              <select
                {...register('rating')}
                className="mt-1 input"
              >
                <option value="">Select rating</option>
                <option value="1">1 - Poor</option>
                <option value="2">2 - Fair</option>
                <option value="3">3 - Good</option>
                <option value="4">4 - Very Good</option>
                <option value="5">5 - Excellent</option>
              </select>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Categories</label>
            <select
              {...register('categories')}
              multiple
              className="mt-1 input"
            >
              {categories.map((category) => (
                <option key={category._id} value={category._id}>
                  {category.name}
                </option>
              ))}
            </select>
            <p className="mt-1 text-xs text-gray-500">
              Hold Ctrl (or Cmd on Mac) to select multiple categories
            </p>
          </div>

  <div>
            <label className="block text-sm font-medium text-gray-700">Notes</label>
            <textarea
              {...register('notes')}
              rows={3}
              className="mt-1 input"
              placeholder="Additional notes about the supplier"
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="btn btn-primary"
            >
              {loading ? 'Saving...' : (isEditing ? 'Update Supplier' : 'Add Supplier')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SupplierModal;
