import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { HiX } from 'react-icons/hi';
import axios from 'axios';
import toast from 'react-hot-toast';

const ProductModal = ({ product, categories, suppliers, onClose, onSaved }) => {
  const [loading, setLoading] = useState(false);
  const [generatingBarcode, setGeneratingBarcode] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    reset,
  } = useForm();

  const isEditing = !!product;

  useEffect(() => {
    if (product) {
      reset({
        ...product,
        category: product.category?._id || '',
        supplier: product.supplier?._id || '',
        location: {
          warehouse: product.location?.warehouse || '',
          aisle: product.location?.aisle || '',
          shelf: product.location?.shelf || '',
          bin: product.location?.bin || '',
        },
      });
    } else {
      reset({
        name: '',
        sku: '',
        description: '',
        category: '',
        supplier: '',
        unit: '',
        costPrice: '',
        sellingPrice: '',
        minStockLevel: '',
        maxStockLevel: '',
        barcode: '',
        tags: '',
        location: { warehouse: '', aisle: '', shelf: '', bin: '' },
      });
    }
  }, [product, reset]);

  const generateBarcode = async () => {
    setGeneratingBarcode(true);
    try {
      const response = await axios.get('/api/barcodes/generate/new');
      setValue('barcode', response.data.barcode);
      toast.success('Barcode generated successfully');
    } catch {
      toast.error('Failed to generate barcode');
    } finally {
      setGeneratingBarcode(false);
    }
  };

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      // Ensure warehouse location is present
      if (!data.location?.warehouse) {
        toast.error('Please provide warehouse location');
        setLoading(false);
        return;
      }
      if (isEditing) {
        await axios.put(`/api/products/${product._id}`, data);
      } else {
        await axios.post('/api/products', data);
      }
      onSaved();
    } catch (error) {
      toast.error(
        error.response?.data?.msg ||
        error.response?.data?.message ||
        'Failed to save product'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-medium text-gray-900">{isEditing ? 'Edit Product' : 'Add New Product'}</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <HiX className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          {/* ... other inputs unchanged ... */}

          {/* Category & Supplier */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Category *</label>
              <select {...register('category', { required: 'Category is required' })} className="mt-1 input">
                <option value="">Select category</option>
                {categories.map((cat) => (
                  <option key={cat._id} value={cat._id}>{cat.name}</option>
                ))}
              </select>
              {errors.category && <p className="mt-1 text-sm text-danger-600">{errors.category.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Supplier</label>
              <select {...register('supplier')} className="mt-1 input">
                <option value="">Select supplier</option>
                {suppliers.map((sup) => (
                  <option key={sup._id} value={sup._id}>{sup.name}</option>
                ))}
              </select>
            </div>
          </div>

          {/* ... other inputs unchanged ... */}

          {/* Warehouse Location (required) */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Warehouse Location *</label>
            <input
              type="text"
              {...register('location.warehouse', { required: 'Please provide warehouse location' })}
              className="mt-1 input"
              placeholder="Enter warehouse location"
            />
            {errors.location?.warehouse && (
              <p className="mt-1 text-sm text-danger-600">{errors.location.warehouse.message}</p>
            )}
          </div>

          {/* ... other inputs and submit/cancel buttons unchanged ... */}
        </form>
      </div>
    </div>
  );
};

export default ProductModal;
